/**
 * üéØ CAMPAIGN 650 INITIALIZATION
 * Load inactive users and prepare segmentation
 */

const campaignConfig = {
  targetUsers: 650,
  expectedROI: { min: 2250, max: 3750 },
  segments: ['CRITICA', 'ALTA', 'MEDIA', 'BAIXA'],
  batchSize: 50,
  delayBetweenBatches: 30000
};

// Get webhook payload
const payload = $input.first().json;

// Initialize campaign session
const campaignSession = {
  id: `campaign_650_${Date.now()}`,
  startTime: new Date(),
  status: 'initializing',
  config: campaignConfig,
  triggerData: payload,
  metadata: {
    platform: 'n8n',
    version: '2.0',
    executedBy: payload.executedBy || 'system'
  }
};

// Log campaign initialization
console.log('üöÄ Campaign 650 initialization started:', campaignSession.id);

return {
  json: {
    campaignSession,
    nextAction: 'load_inactive_users',
    timestamp: new Date().toISOString()
  }
};

/**
 * üß† INTELLIGENT USER SEGMENTATION
 * Segment 650 users by priority for optimal ROI
 */

const users = $input.first().json.users || [];
const campaignSession = $input.first().json.campaignSession;

if (users.length === 0) {
  throw new Error('No inactive users found for campaign');
}

// Segmentation algorithm
function segmentUsers(users) {
  const segments = {
    CRITICA: [],
    ALTA: [],
    MEDIA: [],
    BAIXA: []
  };

  users.forEach(user => {
    const score = calculatePriorityScore(user);

    if (score >= 80) {
      segments.CRITICA.push({ ...user, priority: score, segment: 'CRITICA' });
    } else if (score >= 60) {
      segments.ALTA.push({ ...user, priority: score, segment: 'ALTA' });
    } else if (score >= 40) {
      segments.MEDIA.push({ ...user, priority: score, segment: 'MEDIA' });
    } else {
      segments.BAIXA.push({ ...user, priority: score, segment: 'BAIXA' });
    }
  });

  return segments;
}

function calculatePriorityScore(user) {
  let score = 0;

  // Registration date factor (newer = higher priority)
  const regDate = new Date(user.dataRegistro);
  const daysSinceReg = (Date.now() - regDate.getTime()) / (1000 * 60 * 60 * 24);

  if (daysSinceReg < 90) score += 40;
  else if (daysSinceReg < 180) score += 30;
  else if (daysSinceReg < 365) score += 20;
  else score += 10;

  // Last activity factor
  if (user.ultimaAtividade) {
    const lastActivity = new Date(user.ultimaAtividade);
    const daysSinceActivity = (Date.now() - lastActivity.getTime()) / (1000 * 60 * 60 * 24);

    if (daysSinceActivity < 30) score += 30;
    else if (daysSinceActivity < 60) score += 20;
    else if (daysSinceActivity < 90) score += 10;
  }

  // Payment history factor
  if (user.historicoPagemento) {
    if (user.historicoPagemento >= 6) score += 20;
    else if (user.historicoPagemento >= 3) score += 15;
    else if (user.historicoPagemento >= 1) score += 10;
  }

  // Engagement factor
  if (user.engajamento) {
    score += Math.min(user.engajamento * 10, 20);
  }

  return Math.min(score, 100);
}

const segments = segmentUsers(users);
const totalUsers = Object.values(segments).reduce((sum, segment) => sum + segment.length, 0);

const segmentationResult = {
  campaignId: campaignSession.id,
  totalUsers,
  segments,
  segmentSummary: {
    CRITICA: segments.CRITICA.length,
    ALTA: segments.ALTA.length,
    MEDIA: segments.MEDIA.length,
    BAIXA: segments.BAIXA.length
  },
  qualityScore: totalUsers >= 500 ? 0.95 : 0.8,
  timestamp: new Date().toISOString()
};

console.log('üß† User segmentation completed:', segmentationResult.segmentSummary);

return {
  json: {
    ...segmentationResult,
    campaignSession: {
      ...campaignSession,
      status: 'segmented',
      segmentation: segmentationResult
    },
    nextAction: 'execute_segments'
  }
};

/**
 * üì§ SEGMENT EXECUTION CONTROLLER
 * Process segments by priority with intelligent delays
 */

const data = $input.first().json;
const segments = data.segments;
const campaignSession = data.campaignSession;

// Execution order by priority
const executionOrder = ['CRITICA', 'ALTA', 'MEDIA', 'BAIXA'];
const segmentExecutions = [];

for (const priority of executionOrder) {
  const segment = segments[priority];
  if (!segment || segment.length === 0) continue;

  segmentExecutions.push({
    priority,
    users: segment,
    userCount: segment.length,
    batchSize: campaignSession.config.batchSize,
    delayBetweenBatches: campaignSession.config.delayBetweenBatches,
    expectedConversion: getExpectedConversion(priority),
    executionOrder: executionOrder.indexOf(priority) + 1
  });
}

function getExpectedConversion(priority) {
  const rates = {
    CRITICA: 0.35,
    ALTA: 0.25,
    MEDIA: 0.15,
    BAIXA: 0.08
  };
  return rates[priority] || 0.05;
}

console.log(`üì§ Prepared ${segmentExecutions.length} segments for execution`);

return segmentExecutions.map(segment => ({
  json: {
    segmentExecution: segment,
    campaignSession,
    totalSegments: segmentExecutions.length,
    timestamp: new Date().toISOString()
  }
}));

/**
 * üí∞ REAL-TIME ROI CALCULATION
 * Calculate ROI as segments complete
 */

const segmentResult = $input.first().json;
const campaignSession = segmentResult.campaignSession;

// Investment and revenue calculations
const investment = 1500; // R$ campaign cost
const avgMonthlyValue = 129.90;

function calculateSegmentROI(segmentResult) {
  const priority = segmentResult.segmentExecution.priority;
  const conversionRate = segmentResult.segmentExecution.expectedConversion;
  const monthsProjected = priority === 'BAIXA' ? 3 : 6;

  const sent = segmentResult.sent || 0;
  const expectedReactivations = Math.floor(sent * conversionRate);
  const expectedRevenue = expectedReactivations * avgMonthlyValue * monthsProjected;

  return {
    priority,
    sent,
    expectedReactivations,
    expectedRevenue,
    conversionRate,
    monthsProjected
  };
}

const segmentROI = calculateSegmentROI(segmentResult);

// Update campaign session with segment results
const updatedSession = {
  ...campaignSession,
  segmentResults: {
    ...campaignSession.segmentResults,
    [segmentROI.priority]: segmentROI
  }
};

// Calculate cumulative ROI
const cumulativeRevenue = Object.values(updatedSession.segmentResults || {})
  .reduce((sum, segment) => sum + (segment.expectedRevenue || 0), 0);

const cumulativeROI = ((cumulativeRevenue - investment) / investment) * 100;
const achievesTarget = cumulativeROI >= 2250;

const roiUpdate = {
  campaignId: campaignSession.id,
  segment: segmentROI.priority,
  segmentROI,
  cumulativeRevenue,
  cumulativeROI: Math.round(cumulativeROI),
  achievesTarget,
  investment,
  timestamp: new Date().toISOString()
};

console.log(`üí∞ ROI Update - ${segmentROI.priority}: ${Math.round(cumulativeROI)}% (Target: 2250%+)`);

return {
  json: {
    roiUpdate,
    updatedSession,
    nextAction: 'update_roi_tracking'
  }
};

/**
 * üéâ CAMPAIGN SUCCESS NOTIFICATION
 * Trigger when ROI target is achieved
 */

const data = $input.first().json;
const roiUpdate = data.roiUpdate;

const successNotification = {
  type: 'CAMPAIGN_SUCCESS',
  campaignId: roiUpdate.campaignId,
  achievedROI: roiUpdate.cumulativeROI,
  targetROI: 2250,
  exceededBy: roiUpdate.cumulativeROI - 2250,
  message: `üéâ Campaign 650 SUCCESS! Achieved ${roiUpdate.cumulativeROI}% ROI (Target: 2250%+)`,
  timestamp: new Date().toISOString(),
  actions: [
    'Monitor conversion rates over next 30 days',
    'Prepare follow-up campaigns for non-responders',
    'Analyze high-performing segments',
    'Scale successful strategies'
  ]
};

console.log('üéâ ROI TARGET ACHIEVED!', successNotification.message);

return {
  json: successNotification
};

/**
 * ‚ö†Ô∏è CAMPAIGN OPTIMIZATION ALERT
 * Trigger when ROI target needs optimization
 */

const data = $input.first().json;
const roiUpdate = data.roiUpdate;

const optimizationAlert = {
  type: 'CAMPAIGN_OPTIMIZATION',
  campaignId: roiUpdate.campaignId,
  currentROI: roiUpdate.cumulativeROI,
  targetROI: 2250,
  gapToTarget: 2250 - roiUpdate.cumulativeROI,
  message: `‚ö†Ô∏è Campaign 650 needs optimization. Current ROI: ${roiUpdate.cumulativeROI}% (Target: 2250%+)`,
  timestamp: new Date().toISOString(),
  optimizationActions: [
    'Analyze low-performing segments',
    'Optimize message templates',
    'Increase follow-up frequency',
    'Consider additional incentives',
    'Review targeting criteria'
  ],
  urgency: roiUpdate.cumulativeROI < 1000 ? 'HIGH' : 'MEDIUM'
};

console.log('‚ö†Ô∏è OPTIMIZATION NEEDED:', optimizationAlert.message);

return {
  json: optimizationAlert
};
