parameters.jsCode:
// Segment 650 inactive members by priority
const members = $input.all()[0].json.members || [];
const segments = {
  criticos: [],
  moderados: [],
  baixaFreq: [],
  prospects: []
};

const today = new Date();
const avgMonthlyValue = 129.90;

console.log(`Processing ${members.length} members...`);

members.forEach((member, index) => {
  // Validate phone number
  if (!member.telefone || member.telefone.length < 10) {
    console.log(`Skipping member ${member.nome} - invalid phone`);
    return;
  }

  const lastActivity = new Date(member.ultimaAtividade || member.dataCadastro);
  const daysDiff = Math.floor((today - lastActivity) / (1000 * 60 * 60 * 24));
  const primeiroNome = (member.nome || '').split(' ')[0];

  // Format phone with Brazil code
  const telefone = member.telefone.startsWith('55') ? member.telefone : `55${member.telefone}`;

  const baseData = {
    ...member,
    telefone,
    diasInativo: daysDiff,
    primeiroNome,
    index: index + 1
  };

  if (daysDiff >= 90) {
    segments.criticos.push({
      ...baseData,
      urgencia: 'CRITICA',
      prioridade: 1,
      oferta: `VOLTA POR R$ ${(member.valorPlano * 0.4).toFixed(2)} - 60% OFF!`,
      desconto: 60,
      expectedRevenue: (member.valorPlano || avgMonthlyValue) * 0.35 * 6
    });
  } else if (daysDiff >= 60) {
    segments.moderados.push({
      ...baseData,
      urgencia: 'ALTA',
      prioridade: 2,
      oferta: `${primeiroNome}, volta com 50% OFF!`,
      desconto: 50,
      expectedRevenue: (member.valorPlano || avgMonthlyValue) * 0.25 * 6
    });
  } else if (daysDiff >= 30 || (member.frequenciaMensal || 0) < 8) {
    segments.baixaFreq.push({
      ...baseData,
      urgencia: 'MEDIA',
      prioridade: 3,
      oferta: 'Personal Trainer GRÃTIS + ReavaliaÃ§Ã£o',
      desconto: 0,
      expectedRevenue: (member.valorPlano || avgMonthlyValue) * 0.15 * 6
    });
  } else {
    segments.prospects.push({
      ...baseData,
      urgencia: 'BAIXA',
      prioridade: 4,
      oferta: '7 dias GRÃTIS + AvaliaÃ§Ã£o FÃ­sica',
      desconto: 0,
      expectedRevenue: (member.valorPlano || avgMonthlyValue) * 0.08 * 3
    });
  }
});

console.log('Segmentation completed:', {
  criticos: segments.criticos.length,
  moderados: segments.moderados.length,
  baixaFreq: segments.baixaFreq.length,
  prospects: segments.prospects.length
});

// Return all members sorted by priority
const allMembers = [
  ...segments.criticos,
  ...segments.moderados,
  ...segments.baixaFreq,
  ...segments.prospects
].sort((a, b) => a.prioridade - b.prioridade);

return allMembers;

