    {
      "parameters": {
        "jsCode": "// Calculate Campaign ROI and Results\nconst results = $input.all();\nconst sent = results.filter(r => {\n  try {\n    return r.json.success === true || (r.json.status && r.json.status >= 200 && r.json.status < 300);\n  } catch {\n    return false;\n  }\n}).length;\n\nconst errors = results.length - sent;\nconst avgMonthlyValue = 129.90;\n\nconst conversionRates = {\n  CRITICA: 0.35,\n  ALTA: 0.25,\n  MEDIA: 0.15,\n  BAIXA: 0.08\n};\n\nlet expectedRevenue = 0;\nlet expectedNewMembers = 0;\nconst segmentBreakdown = {\n  CRITICA: { sent: 0, expected: 0 },\n  ALTA: { sent: 0, expected: 0 },\n  MEDIA: { sent: 0, expected: 0 },\n  BAIXA: { sent: 0, expected: 0 }\n};\n\nresults.forEach(result => {\n  try {\n    const isSuccess = result.json.success === true || (result.json.status && result.json.status >= 200 && result.json.status < 300);\n    \n    if (isSuccess && result.json.segment) {\n      const segment = result.json.segment;\n      const rate = conversionRates[segment] || 0.08;\n      const months = (segment === 'CRITICA' || segment === 'ALTA') ? 6 : 3;\n      \n      expectedRevenue += avgMonthlyValue * rate * months;\n      expectedNewMembers += rate;\n      \n      if (segmentBreakdown[segment]) {\n        segmentBreakdown[segment].sent++;\n        segmentBreakdown[segment].expected += rate;\n      }\n    }\n  } catch (error) {\n    console.log('Error processing result:', error);\n  }\n});\n\nconst investment = 1500;\nconst roi = ((expectedRevenue - investment) / investment) * 100;\nconst successRate = sent > 0 ? ((sent / results.length) * 100) : 0;\n\nconst report = {\n  timestamp: new Date().toISOString(),\n  campaign: 'Campaign 650 - WAHA N8N',\n  platform: 'N8N + WAHA Cloud',\n  execution: {\n    totalProcessed: results.length,\n    sent,\n    errors,\n    successRate: successRate.toFixed(1) + '%'\n  },\n  roi: {\n    investment,\n    expectedRevenue: expectedRevenue.toFixed(2),\n    roi: roi.toFixed(0) + '%',\n    expectedNewMembers: Math.floor(expectedNewMembers)\n  },\n  segments: segmentBreakdown,\n  performance: {\n    avgMonthlyValue,\n    conversionRates,\n    projectedMonthlyRevenue: (expectedNewMembers * avgMonthlyValue).toFixed(2)\n  }\n};\n\nconsole.log('Campaign 650 Results:', report);\n\nreturn report;"
      },
      "name": "Calculate ROI",
      "type": "n8n-nodes-base.code",
      "typeVersion": 2,
      "position": [1450, 300],
      "id": "calculate-roi"
    },
    {
      "parameters": {
