/**
 * ðŸ“Š ACTIVE CAMPAIGNS PROCESSOR
 * Process multiple active campaigns for ROI tracking
 */

const response = $input.first().json;
const activeCampaigns = response.campaigns || [];

if (activeCampaigns.length === 0) {
  console.log('â„¹ï¸ No active campaigns found for ROI tracking');
  return [];
}

// Process each campaign for individual ROI tracking
const campaignProcesses = activeCampaigns.map(campaign => {
  return {
    campaignId: campaign.id,
    name: campaign.name,
    status: campaign.status,
    startDate: campaign.startDate,
    targetUsers: campaign.targetUsers,
    expectedROI: campaign.expectedROI,
    currentMetrics: campaign.currentMetrics || {},
    lastUpdated: new Date().toISOString(),
    trackingEnabled: true
  };
});

console.log(`ðŸ“Š Processing ROI tracking for ${campaignProcesses.length} active campaigns`);

return campaignProcesses.map(campaign => ({ json: campaign }));

/**
 * ðŸ’° REAL-TIME ROI CALCULATOR
 * Calculate live ROI metrics for dashboard display
 */

const campaignData = $input.first().json;
const metrics = campaignData.metrics || {};

// ROI Calculation Constants
const INVESTMENT = 1500; // R$ campaign cost
const AVG_MONTHLY_VALUE = 129.90;
const CONVERSION_RATES = {
  CRITICA: 0.35,
  ALTA: 0.25,
  MEDIA: 0.15,
  BAIXA: 0.08
};

function calculateLiveROI(metrics) {
  let totalRevenue = 0;
  let totalReactivations = 0;
  const segmentAnalysis = {};

  // Calculate by segment
  Object.entries(metrics.segments || {}).forEach(([segment, data]) => {
    const conversionRate = CONVERSION_RATES[segment] || 0.1;
    const monthsProjected = segment === 'BAIXA' ? 3 : 6;

    const sent = data.sent || 0;
    const errors = data.errors || 0;
    const reactivations = Math.floor(sent * conversionRate);
    const revenue = reactivations * AVG_MONTHLY_VALUE * monthsProjected;

    segmentAnalysis[segment] = {
      sent,
      errors,
      successRate: sent > 0 ? ((sent / (sent + errors)) * 100).toFixed(1) : 0,
      expectedReactivations: reactivations,
      expectedRevenue: revenue,
      conversionRate: (conversionRate * 100).toFixed(1)
    };

    totalRevenue += revenue;
    totalReactivations += reactivations;
  });

  // Calculate overall ROI
  const roi = ((totalRevenue - INVESTMENT) / INVESTMENT) * 100;
  const achievesTarget = roi >= 2250;

  return {
    campaignId: campaignData.campaignId,
    timestamp: new Date().toISOString(),
    investment: INVESTMENT,
    projectedRevenue: totalRevenue,
    roi: Math.round(roi),
    achievesTarget,
    expectedReactivations: totalReactivations,
    segmentAnalysis,
    performance: {
      totalSent: Object.values(segmentAnalysis).reduce((sum, seg) => sum + seg.sent, 0),
      totalErrors: Object.values(segmentAnalysis).reduce((sum, seg) => sum + seg.errors, 0),
      overallSuccessRate: calculateOverallSuccessRate(segmentAnalysis)
    },
    targets: {
      minROI: 2250,
      maxROI: 3750,
      progressToMin: Math.min((roi / 2250) * 100, 100),
      progressToMax: Math.min((roi / 3750) * 100, 100)
    }
  };
}

function calculateOverallSuccessRate(segmentAnalysis) {
  const totalSent = Object.values(segmentAnalysis).reduce((sum, seg) => sum + seg.sent, 0);
  const totalErrors = Object.values(segmentAnalysis).reduce((sum, seg) => sum + seg.errors, 0);

  return totalSent > 0 ? ((totalSent / (totalSent + totalErrors)) * 100).toFixed(1) : 0;
}

const roiCalculation = calculateLiveROI(metrics);

// Determine alert level
let alertLevel = 'success';
if (roiCalculation.roi < 1000) alertLevel = 'critical';
else if (roiCalculation.roi < 2000) alertLevel = 'warning';
else if (roiCalculation.roi < 2250) alertLevel = 'info';

const dashboardData = {
  ...roiCalculation,
  alertLevel,
  campaignName: campaignData.name,
  status: campaignData.status,
  lastCalculated: new Date().toISOString(),
  dashboardUpdate: true
};

console.log(`ðŸ’° ROI calculated for ${campaignData.campaignId}: ${roiCalculation.roi}% (${alertLevel})`);

return {
  json: dashboardData
};

/**
 * ðŸŽ‰ ROI SUCCESS DASHBOARD UPDATE
 * Update dashboard with success metrics
 */

const roiData = $input.first().json;

const successDashboard = {
  type: 'ROI_SUCCESS',
  campaignId: roiData.campaignId,
  roi: roiData.roi,
  status: 'TARGET_ACHIEVED',
  achievement: {
    exceededTarget: roiData.roi - 2250,
    progressToMax: roiData.targets.progressToMax,
    reactivations: roiData.expectedReactivations,
    revenue: roiData.projectedRevenue
  },
  dashboard: {
    title: `ðŸŽ‰ Campaign Success: ${roiData.roi}% ROI`,
    subtitle: `Target achieved! ${roiData.expectedReactivations} expected reactivations`,
    color: 'success',
    priority: 'high',
    timestamp: new Date().toISOString()
  },
  metrics: roiData.segmentAnalysis,
  nextActions: [
    'Monitor conversion rates over next 30 days',
    'Scale successful strategies to other campaigns',
    'Analyze top-performing segments',
    'Prepare victory report for stakeholders'
  ]
};

console.log('ðŸŽ‰ ROI SUCCESS: Dashboard updated with achievement metrics');

return {
  json: successDashboard
};

/**
 * ðŸ“Š STANDARD ROI DASHBOARD UPDATE
 * Update dashboard with current progress
 */

const roiData = $input.first().json;

const standardDashboard = {
  type: 'ROI_PROGRESS',
  campaignId: roiData.campaignId,
  roi: roiData.roi,
  status: 'IN_PROGRESS',
  progress: {
    toMinTarget: roiData.targets.progressToMin,
    toMaxTarget: roiData.targets.progressToMax,
    currentReactivations: roiData.expectedReactivations,
    currentRevenue: roiData.projectedRevenue
  },
  dashboard: {
    title: `ðŸ“Š Campaign Progress: ${roiData.roi}% ROI`,
    subtitle: `${roiData.targets.progressToMin.toFixed(1)}% to target (${roiData.expectedReactivations} reactivations)`,
    color: roiData.alertLevel === 'critical' ? 'danger' : 
           roiData.alertLevel === 'warning' ? 'warning' : 'info',
    priority: roiData.alertLevel === 'critical' ? 'high' : 'normal',
    timestamp: new Date().toISOString()
  },
  segments: Object.entries(roiData.segmentAnalysis).map(([segment, data]) => ({
    name: segment,
    sent: data.sent,
    successRate: data.successRate,
    expectedReactivations: data.expectedReactivations,
    expectedRevenue: data.expectedRevenue
  })),
  recommendations: generateRecommendations(roiData)
};

function generateRecommendations(roiData) {
  const recommendations = [];

  if (roiData.roi < 1000) {
    recommendations.push('ðŸš¨ Critical: Review message templates and targeting');
    recommendations.push('ðŸ“ˆ Consider increasing incentives or discounts');
    recommendations.push('ðŸ”„ Implement immediate follow-up sequences');
  } else if (roiData.roi < 2000) {
    recommendations.push('âš ï¸ Optimize low-performing segments');
    recommendations.push('ðŸ“± Increase message frequency for CRITICA segment');
    recommendations.push('ðŸ’¡ A/B test different message approaches');
  } else {
    recommendations.push('âœ… Campaign performing well, monitor closely');
    recommendations.push('ðŸŽ¯ Consider scaling successful strategies');
    recommendations.push('ðŸ“Š Document best practices for future campaigns');
  }

  return recommendations;
}

console.log(`ðŸ“Š STANDARD UPDATE: Dashboard updated with ${roiData.alertLevel} alert level`);

return {
  json: standardDashboard
};

/**
 * ðŸ“ˆ ANALYTICS DATA PROCESSOR
 * Process dashboard data for analytics storage
 */

const dashboardData = $input.first().json;

const analyticsPayload = {
  type: 'roi_tracking',
  source: 'n8n_dashboard',
  timestamp: new Date().toISOString(),
  campaignId: dashboardData.campaignId,
  metrics: {
    roi: dashboardData.roi,
    expectedReactivations: dashboardData.progress?.currentReactivations || dashboardData.achievement?.reactivations,
    expectedRevenue: dashboardData.progress?.currentRevenue || dashboardData.achievement?.revenue,
    alertLevel: dashboardData.dashboard.color,
    achievesTarget: dashboardData.type === 'ROI_SUCCESS'
  },
  performance: {
    progressToMin: dashboardData.progress?.toMinTarget || 100,
    progressToMax: dashboardData.progress?.toMaxTarget || dashboardData.achievement?.progressToMax
  },
  segments: dashboardData.segments || [],
  timestamp_hour: new Date().getHours(),
  timestamp_day: new Date().getDate(),
  real_time: true
};

console.log('ðŸ“ˆ Analytics data processed for storage');

return {
  json: analyticsPayload
};
