// CARREGAR DADOS CSV LOCAIS - SEM API
const fs = require('fs');
const path = require('path');

// Caminhos dos arquivos CSV exportados manualmente
const csvPaths = {
  todosAlunos: '/home/node/.n8n/csv-data/todos_alunos.csv',
  alunosAtivos: '/home/node/.n8n/csv-data/alunos_ativos.csv',
  alunosInativos: '/home/node/.n8n/csv-data/alunos_inativos.csv'
};

function parseCSV(csvContent) {
  const lines = csvContent.split('\n').filter(line => line.trim());
  const headers = lines[0].split(',').map(h => h.trim().replace(/"/g, ''));

  return lines.slice(1).map(line => {
    const values = line.split(',').map(v => v.trim().replace(/"/g, ''));
    const obj = {};
    headers.forEach((header, index) => {
      obj[header] = values[index] || '';
    });
    return obj;
  });
}

let dadosCarregados = {
  todos: [],
  ativos: [],
  inativos: [],
  status: 'sucesso',
  timestamp: new Date().toISOString()
};

try {
  // Verificar se arquivos existem
  Object.keys(csvPaths).forEach(key => {
    const csvPath = csvPaths[key];
    if (fs.existsSync(csvPath)) {
      const csvContent = fs.readFileSync(csvPath, 'utf8');
      const dados = parseCSV(csvContent);

      if (key === 'todosAlunos') dadosCarregados.todos = dados;
      if (key === 'alunosAtivos') dadosCarregados.ativos = dados;
