  moderados: [],
  recentes: [],
  outros: []
};

inativos.forEach((aluno, index) => {
  const nome = aluno['Nome-Completo'] || aluno.Nome || `Aluno_${index}`;
  const telefone = (aluno['Telefone-1'] || aluno.Telefone || '').replace(/\D/g, '');
  const email = aluno['E-mail'] || aluno.Email || '';
  const dataCadastro = aluno['Data-de-Cadastro'] || aluno['Data_Cadastro'] || '';

  // Validar telefone
  if (!telefone || telefone.length < 10) {
    grupos.outros.push({ nome, telefone, motivo: 'Telefone invÃ¡lido' });
    return;
  }

  // Determinar grupo baseado na data de cadastro
  let grupo = 'outros';
  if (dataCadastro) {
    const [day, month, year] = dataCadastro.split(/[\s\/\-]/)[0].split('/');
