    const mes = cadastroDate.getMonth() + 1;
    const ano = cadastroDate.getFullYear();

    if (ano === 2025) {
      if (mes >= 2 && mes <= 3) grupo = 'criticos';      // Fev-Mar
      else if (mes >= 4 && mes <= 6) grupo = 'moderados'; // Abr-Jun  
      else if (mes >= 7 && mes <= 9) grupo = 'recentes';  // Jul-Set
